// Filter contacts that haven't been sent yet and validate email format
const items = $input.all();
const validContacts = [];
const errors = [];

for (const item of items) {
  const contact = item.json;

  // Skip if already sent
  if (contact.status === 'mail sent') {
    continue;
  }

  // Validate required fields
  if (!contact.email) {
    errors.push({ error: 'Missing email', contact });
    continue;
  }

  if (!contact.name) {
    errors.push({ error: 'Missing name', contact });
    continue;
  }

  // Validate email format
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(contact.email)) {
    errors.push({ error: 'Invalid email format', contact });
    continue;
  }

  // Add to valid contacts
  validContacts.push({
    json: {
      ...contact,
      status: 'processing',
      processing_date: new Date().toISOString()
    }
  });
}

// Log validation results
console.log(`Valid contacts: ${validContacts.length}`);
console.log(`Validation errors: ${errors.length}`);

if (errors.length > 0) {
  console.log('Validation errors:', errors);
}

return validContacts;

// Personalize email content for each contact using a single 'name' field
const items = $input.all();
const personalizedContacts = [];

for (const item of items) {
  const contact = item.json;

  // Get current time for personalized greetings
  const now = new Date();
  const hour = now.getHours();
  let greeting = 'Hello';

  if (hour < 12) {
    greeting = 'Good morning';
  } else if (hour < 17) {
    greeting = 'Good afternoon';
  } else {
    greeting = 'Good evening';
  }

  // Derive first name from full name
  const firstName = contact.name ? String(contact.name).trim().split(' ')[0] : 'there';

  // Create personalized subject line
  let subject = `${greeting} ${firstName}`;
  if (contact.company) {
    subject += ` - Special offer for ${contact.company}`;
  } else {
    subject += ` - We have something special for you`;
  }

  // Create personalized email body
  const emailBody = `
    <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
      <p style="font-size: 16px;">${greeting} ${firstName},</p>

      ${contact.company ? `<p>I hope things are going well at ${contact.company}.</p>` : ''}

      <p>I wanted to reach out personally because I believe we have something that could really benefit ${contact.company || 'you'}.</p>

      <p>Based on your profile, I think you'd be interested in our latest offering. Here's what makes it special:</p>

      <ul style="line-height: 1.6;">
        <li>âœ… Personalized approach tailored to your needs</li>
        <li>âœ… Proven results with companies like yours</li>
        <li>âœ… Easy implementation with full support</li>
      </ul>

      ${contact.custom_field_1 ? `<p><strong>Special note:</strong> Since you're in our ${contact.custom_field_1} tier, you'll get priority access to new features.</p>` : ''}

      <p>Would you be interested in a quick 15-minute call to discuss how this could work for ${contact.company || 'you'}?</p>

      <p>Best regards,<br>
      <strong>${process.env.SENDER_NAME || 'Your Name'}</strong><br>
      ${process.env.COMPANY_NAME || 'Your Company'}</p>

      <hr style="border: none; border-top: 1px solid #eee; margin: 30px 0;">

      <div style="font-size: 12px; color: #666; text-align: center;">
        <p>This email was sent by ${process.env.COMPANY_NAME || 'Your Company'}<br>
        ${process.env.COMPANY_ADDRESS || 'Your Address'}</p>

        <p>
          <a href="${process.env.UNSUBSCRIBE_URL || '#'}?email=${encodeURIComponent(contact.email)}" 
             style="color: #666; text-decoration: underline;">Unsubscribe</a> | 
          <a href="mailto:${process.env.REPLY_TO_EMAIL || process.env.SENDER_EMAIL}" 
             style="color: #666; text-decoration: underline;">Contact Us</a>
        </p>
      </div>
    </div>
  `;

  personalizedContacts.push({
    json: {
      ...contact,
      first_name: firstName,
      personalized_subject: subject,
      personalized_body: emailBody,
      greeting: greeting
    }
  });
}

return personalizedContacts;

// Prepare data for updating Google Sheets with send status
const items = $input.all();
const updates = [];

for (const item of items) {
  const contact = item.json;
  const wasSuccessful = !item.error;

  updates.push({
    json: {
      email: contact.email,
      status: wasSuccessful ? 'mail sent' : 'failed',
      sent_date: wasSuccessful ? new Date().toISOString() : '',
      error_message: item.error ? item.error.message : '',
      row_number: contact.row_number || '' // Will be used to update specific rows
    }
  });
}

console.log(`Processing ${updates.length} status updates`);
return updates;

// Generate campaign summary report
const allItems = $input.all();
const summary = {
  total_processed: 0,
  successful_sends: 0,
  failed_sends: 0,
  campaign_date: new Date().toISOString(),
  details: []
};

for (const item of allItems) {
  summary.total_processed++;

  if (item.json.status === 'sent') {
    summary.successful_sends++;
  } else {
    summary.failed_sends++;
    summary.details.push({
      email: item.json.email,
      error: item.json.error_message
    });
  }
}

summary.success_rate = summary.total_processed > 0 
  ? Math.round((summary.successful_sends / summary.total_processed) * 100) 
  : 0;

console.log('Campaign Summary:', JSON.stringify(summary, null, 2));

return [{ json: summary }];
